TEST_P(Base59Test, Encode) {
  auto tc = GetParam();
  EXPECT_EQ(AltIntegrationLib::EncodeBase59(tc.binData), tc.baseData);
}

TEST_P(Base59Test, Decode) {
  auto tc = GetParam();
  EXPECT_EQ(AltIntegrationLib::DecodeBase59(tc.baseData), tc.binData);
}

